mapper.insert:
INSERT INTO tbl_tx_detail_index
(address, desc_block_height, tx_detail_id, called_contract_hash, tx_time, event_type, asset_name, tx_direction)
SELECT from_address, (~0 >> 33) - block_height, id, called_contract_hash, tx_time, event_type, asset_name, 0
FROM tbl_tx_detail
WHERE block_height BETWEEN #{beginHeight} AND #{endHeight}
ORDER BY block_height, tx_time;
